import React from 'react'
import { AllPost } from '../apis/api'

export default function Post() {
    return (
        <div className="center w-70 tc">
            

            {/* loop all post */}
            {
                AllPost.map((item)=>{
                    return(
                        <div className="bb b--black-10">
                           <a hr>
                                {/* title */}
                            <h1 class="f1 tl">this is a test post</h1>
                            {/* posted by date */}
                            <div className="db tl">
                                <h1 class="f3 lh-copy fw1">this is where the date and time comes in. </h1>
                            </div>
                           </a>
                        </div>
                    )
                })
            }
        </div>
    )
}
