    const res = await fetchALLPost.json()
    return (
        <div className="center w-70 tc">
            

            {/* loop all post */}
            {
                AllPost.map((item)=>{
                    return(
                        <div className="bb b--black-10">
                           <a href={`/post/${item.id}`} className="link">
                                {/* title */}
                            <h1 class="f1 tl black">{item.title}</h1>
                            {/* posted by date */}
                            <div className="db tl">
                                <h1 class="f3 lh-copy fw1 gray">this is where the date and time comes in. </h1>
                            </div>
                           </a>
                        </div>
                    )
                })
            }
        </div>
    )
}
