                <textarea 
                onChange={e=>settext(e.target.value)}
                className="pa2" value={text} cols="80" rows="3" placeholder="Write comment here..."/>
                 <button class="b ph3 pv2 input-reset ba b--black bg-transparent grow pointer f6 dib" 
